catalog_script_client.script:
function onSubmit() {
    //Type appropriate comment here, and begin script below
    var name = g_form.getValue('full_name');
    var phone = g_form.getValue('phone');
    var departure_city = g_form.getValue('departure_city');
    var destination_city = g_form.getValue('destination_city');
    var passport_number = g_form.getValue('passport_number');



    var ga = new GlideAjax('DataTrans');
    ga.addParam('sysparm_name', 'initializeTask');

    ga.addParam('name', name);
	ga.addParam('phone', phone);
    ga.addParam('departure_city', departure_city);
    ga.addParam('destination_city', destination_city);
    ga.addParam('passport_number', passport_number);
    ga.getXMLAnswer(function(response) {
        if (response) {
            g_form.addInfoMessage("done")
        } else {
            g_form.addErrorMessage("Error")
        }
    });

}
